import React, {useState} from 'react'
import {View, Button, TextInput, ScrollView, StyleSheet} from 'react-native';
import firebase from '../database/firebase.js'


const CrearMedicamento = (props) => {
    
    const [state, setState] = useState({
        name: "",
        email: "",
        phone: ""
    });

    const handleChangeText = (name,value) => {
        setState({ ...state, [name]: value});
    };
    
    const SaveNewUser = async () => {
    
        if(state.name===''){
            alert('Porfavor Ingrese nombre')
        }
        else{
            
            try {
                await firebase.db.collection('users').add({
              
                    name:  state.name,
                    email: state.email,
                    phone: state.phone
                })
                props.navigation.navigate('ListarMedicamento');
            } catch (error) {
                console.log(error);
            }
        }
    }
    return (
        <ScrollView style={styles.container}>
            <View style={styles.inputGroup}>
                <TextInput placeholder="Nombre Usuario" 
                onChangeText={(value) => handleChangeText('name',value)}  />
            </View>
            <View style={styles.inputGroup}>
                <TextInput placeholder="Email Usuario"
                onChangeText={(value) => handleChangeText('email',value)}/>
            </View>
            <View style={styles.inputGroup}>
                <TextInput placeholder="Phone Usuario"
                onChangeText={(value) => handleChangeText('phone',value)}/>
            </View>
            <View style={styles.inputGroup}>
                <Button title="Guardar Usuario" onPress={() =>  SaveNewUser()}/>
            </View>
        </ScrollView>
       
    )
}

const  styles = StyleSheet.create({
    container:{
        flex:1,
        padding:35
    },
    inputGroup: {
        flex: 1,
        padding:0,
        marginBottom:15,
        borderBottomWidth:1,
        borderBottomColor: '#cccccc'
    }
})
export default CrearMedicamento
