import React, { useState, useEffect } from "react";
import { Button, StyleSheet } from "react-native";
import { ListItem, Avatar } from "react-native-elements";
import { ScrollView } from "react-native-gesture-handler";

import firebase from "../database/firebase";

const ListarMedicamento = (props) => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
      firebase.db.collection("users").onSnapshot((querySnapshot) => {
        const users = [];
        querySnapshot.docs.forEach((doc) => {
          const { nombre, precio,marca, descripcion } = doc.data();
          users.push({
            id: doc.id,
            nombre,
            precio,
            marca,
            descripcion,
          });
        });
        setUsers(users);
      });
    }, []);
  

    return (
        
        <ScrollView>
         <Text style={styles.title}>pablo</Text>
        <Image 
            source={{uri: 'https://picsum.photos/200/200'}}
            style={styles.image}
        />
          {users.map((user) => {
            return (
              <ListItem
                key={user.id}
                bottomDivider
                onPress={() => {
                  props.navigation.navigate("verMedicamento", {
                    userId: user.id,
                  });
                }}
              >
                <ListItem.Chevron />
                <Avatar
                  source={{
                    uri:
                      "https://png.pngtree.com/png-vector/20190803/ourlarge/pngtree-medicine-pill-capsule-drugs-tablet-flat-color-icon-vector-png-image_1647346.jpg",
                  }}
                  rounded
                />
                <ListItem.Content>
                  <ListItem.Title>{user.nombre}</ListItem.Title>
                  <ListItem.Subtitle>{user.marca}</ListItem.Subtitle>
                  <ListItem.Subtitle>{user.precio}</ListItem.Subtitle>
                </ListItem.Content>
              </ListItem>
            );
          })}
        </ScrollView>
      );
    };
    
export default ListarMedicamento;
