import React, { useState, useEffect } from "react";
import { Button, StyleSheet } from "react-native";
import { ListItem, Avatar } from "react-native-elements";
import { ScrollView } from "react-native-gesture-handler";

import firebase from "../database/firebase";

const ListarMedicamento = (props) => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
      firebase.db.collection("users").onSnapshot((querySnapshot) => {
        const users = [];
        querySnapshot.docs.forEach((doc) => {
          const { nombre, precio,marca, descripcion } = doc.data();
          users.push({
            id: doc.id,
            nombre,
            precio,
            descripcion,
          });
        });
        setUsers(users);
      });
    }, []);
  

    return (
        
        <ScrollView>
          <Button
            onPress={() => props.navigation.navigate("CrearMedicamento")}
            title="Registrar Medicamento"
          />
          {users.map((user) => {
            return (
              <ListItem
                key={user.id}
                bottomDivider
                onPress={() => {
                  props.navigation.navigate("verMedicamento", {
                    userId: user.id,
                  });
                }}
              >
                <ListItem.Chevron />
                <Avatar
                  source={{
                    uri:
                      "https://upload.wikimedia.org/wikipedia/commons/thumb/1/12/User_icon_2.svg/1200px-User_icon_2.svg.png",
                  }}
                  rounded
                />
                <ListItem.Content>
                  <ListItem.Title>{user.nombre}</ListItem.Title>
                  <ListItem.Subtitle>{user.precio}</ListItem.Subtitle>
                </ListItem.Content>
              </ListItem>
            );
          })}
        </ScrollView>
      );
    };
    
export default ListarMedicamento;
